import{_ as e,a as i}from"./chunks/android.BdYrGj4l.js";import{_ as a,a as o}from"./chunks/android_showcase.lCf-1Xyl.js";import{_ as n,c as l,o as s,ag as d}from"./chunks/framework.BDwTZuFy.js";const x=JSON.parse('{"title":"Installing Sandbox App","description":"","frontmatter":{},"headers":[],"relativePath":"guides/quick-start/install-native-app.md","filePath":"guides/quick-start/install-native-app.md","lastUpdated":1748427295000}'),r={name:"guides/quick-start/install-native-app.md"};function p(h,t,u,c,g,m){return s(),l("div",null,t[0]||(t[0]=[d('<h1 id="installing-sandbox-app" tabindex="-1">Installing Sandbox App <a class="header-anchor" href="#installing-sandbox-app" aria-label="Permalink to &quot;Installing Sandbox App&quot;">​</a></h1><p>The Granite Sandbox App is an application for testing deployed bundles. This document guides you through installing and setting up the Granite Sandbox App on iOS simulators and Android devices.</p><h2 id="ios-simulator-installation-guide" tabindex="-1"><span style="display:inline-flex;align-items:center;gap:5px;"><img src="'+e+'" alt="Apple iOS" width="24" height="24" style="margin-top:-4px;"> iOS Simulator Installation Guide </span> <a class="header-anchor" href="#ios-simulator-installation-guide" aria-label="Permalink to &quot;&lt;span style=&quot;display:inline-flex; align-items:center; gap:5px;&quot;&gt;&lt;img src=&quot;/icons/apple.svg&quot; alt=&quot;Apple iOS&quot; width=&quot;24&quot; height=&quot;24&quot; style=&quot;margin-top:-4px&quot;&gt; iOS Simulator Installation Guide &lt;/span&gt;&quot;">​</a></h2><p>To test React Native apps on iOS, you need a simulator. The simulator comes with <code>Xcode</code> when installed on macOS.</p><p>Follow the guide below to install the app.</p><h3 id="prerequisites" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>macOS</li><li>Xcode (Available on the App Store)</li></ul><h3 id="installation-steps" tabindex="-1">Installation Steps <a class="header-anchor" href="#installation-steps" aria-label="Permalink to &quot;Installation Steps&quot;">​</a></h3><ol><li><p>Download the Granite iOS app bundle from the link below.<br><a href="/download/granite_ios.zip">granite_ios.zip</a></p></li><li><p>Extract the downloaded <code>granite_ios.zip</code> file.</p></li><li><p>Drag the <code>.app</code> file into the <code>Xcode</code> iOS simulator window to install.<br> The app will be copied to the simulator when you drag and drop.</p></li></ol><h2 id="android-installation-guide" tabindex="-1"><span style="display:inline-flex;align-items:center;gap:5px;"><img src="'+i+'" alt="Android" width="24" height="24" style="margin-top:-2px;"> Android Installation Guide </span> <a class="header-anchor" href="#android-installation-guide" aria-label="Permalink to &quot;&lt;span style=&quot;display:inline-flex; align-items:center; gap:5px;&quot;&gt;&lt;img src=&quot;/icons/android.svg&quot; alt=&quot;Android&quot; width=&quot;24&quot; height=&quot;24&quot; style=&quot;margin-top:-2px&quot;&gt; Android Installation Guide &lt;/span&gt;&quot;">​</a></h2><h3 id="prerequisites-1" tabindex="-1">Prerequisites <a class="header-anchor" href="#prerequisites-1" aria-label="Permalink to &quot;Prerequisites&quot;">​</a></h3><ul><li>Android device or emulator</li><li>ADB (Android Debug Bridge) installation (Comes with Android Studio)</li></ul><h3 id="installation-steps-1" tabindex="-1">Installation Steps <a class="header-anchor" href="#installation-steps-1" aria-label="Permalink to &quot;Installation Steps&quot;">​</a></h3><ol><li><p>Download the Granite Android app bundle from the link below.<br><a href="/download/granite_android.zip">granite_android.zip</a></p></li><li><p>Extract the downloaded <code>granite_android.zip</code> file.</p></li><li><p>Choose one of the installation methods.</p></li></ol><h4 id="install-using-adb-command" tabindex="-1">Install using ADB command <a class="header-anchor" href="#install-using-adb-command" aria-label="Permalink to &quot;Install using ADB command&quot;">​</a></h4><ul><li>Connect your Android device to your computer, then run the following command in the terminal:<div class="language-bash vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang">bash</span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span style="--shiki-light:#6F42C1;--shiki-dark:#B392F0;">adb</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> install</span><span style="--shiki-light:#032F62;--shiki-dark:#9ECBFF;"> granite_android.zip</span></span></code></pre></div></li></ul><h4 id="direct-installation-on-device" tabindex="-1">Direct installation on device <a class="header-anchor" href="#direct-installation-on-device" aria-label="Permalink to &quot;Direct installation on device&quot;">​</a></h4><ol><li>Copy the downloaded APK file to your Android device.</li><li>Open the file explorer on your device and tap the APK file to install.<br> You may need to enable &#39;Install from Unknown Sources&#39; in security settings.</li></ol><h2 id="initial-setup" tabindex="-1">Initial Setup <a class="header-anchor" href="#initial-setup" aria-label="Permalink to &quot;Initial Setup&quot;">​</a></h2><p>When you first run the app, you need to enter the CDN address for loading bundles and the deep link scheme. Granite provides values that you can use for testing.</p><table tabindex="0"><thead><tr><th>Setting Item</th><th>Value</th><th>Description</th></tr></thead><tbody><tr><td><strong>Host</strong></td><td><code>https://d2dzky5bdhec40.cloudfront.net</code></td><td>Trial bundle CDN address. Provided for testing.</td></tr><tr><td><strong>URL Scheme</strong></td><td><code>granite://showcase</code> <br> <code>granite://counter</code></td><td>Scheme used for handling deep links in the app.</td></tr></tbody></table><p>Enter these values in the Granite sandbox settings and run the app.</p><table tabindex="0"><thead><tr><th>iOS</th><th>Android</th></tr></thead><tbody><tr><td><video autoplay loop muted style="max-width:400px;width:100%;height:auto;margin-top:1rem;"><source src="'+a+'" type="video/mp4"> Your browser does not support the video tag. </video></td><td><video autoplay loop muted style="max-width:400px;width:100%;height:auto;margin-top:1rem;"><source src="'+o+'" type="video/mp4"> Your browser does not support the video tag. </video></td></tr></tbody></table>',23)]))}const y=n(r,[["render",p]]);export{x as __pageData,y as default};
